// Start Header
newFunction();

function newFunction() {
    var item = document.querySelectorAll('.icon');
    var itemCounter = item.length;
    var count = 4;
    setInterval(() => {
        if (count == itemCounter) {
            count = 0;
        }
        for (var i = 0; i < item.length; i++) {
            item[i].classList.remove("active");
        }
        item[count].classList.add("active");
        var svg = item[count].innerHTML;
        document.querySelectorAll('#main-icon')[0].children[0].remove();
        document.querySelectorAll('#main-icon')[0].innerHTML = svg;
        var Tilte_Service = document.querySelector("#title-data");
        var info_Service = document.querySelector("#info-data");
        Tilte_Service.innerHTML = item[count].dataset.title;
        info_Service.innerHTML = item[count].dataset.info;
        count++;

    }, 10000);
}
